import React, {Component} from "react";
import {Table} from "react-bootstrap";
import "./TableData.css";
import {connect} from "react-redux";
import {TemplateButton} from "../TemplateButton/TemplateButton";
import {PagesButton} from "../PagesButton/PagesButton";


class TableData extends Component {
  render() {
    return (
      <>
        <Table bordered>
          <thead className={"tbl-border"}>
          <tr className={"tbl-header"}>
            <th>ID</th>
            <th>Заголовок</th>
            <th>Описание</th>
          </tr>
          </thead>
          <tbody>
          {this.props.posts.map((item, key) => {
            const {userId, id, title, body} = item;


            if (userId === 1) {
              return (
                <>
                  <tr className={id !== "" && title !== "" && body !== "" ? "full-content" : "empty-content"}>
                    <td key={key} className={"id-format"}>{id}</td>
                    <td key={key} className={"title-format"}>{title}</td>
                    <td key={key} className={"content-format"}>{body}</td>
                  </tr>
                </>
              )
            }


          })}

          </tbody>
        </Table>
        <div>
          <TemplateButton styleBtn={"link"} content={"Назад"}/>
          <PagesButton allPosts={this.props.posts.length} countPostsOnPage={10}/>
          <TemplateButton styleBtn={"link"} content={"Далее"}/>
        </div>
      </>
    );
  }


}

export default connect(
  state => ({
    posts: state.posts,
  }),
  dispatch => ({
    onAddRedux: (item) => {
      dispatch({type: 'ADD_TO_REDUX', data: item})
    },
  })
)(TableData);
