import React, { Component } from "react";
import {connect} from "react-redux";
import "./FilterData.css";

class FilterData extends Component {
  render() {
    const allPosts = this.props.posts;
    const sortId = () => {
      const reverse = allPosts.sort().reverse();
      console.log(reverse);
      this.props.onUpdateAllPosts(reverse);
    }


    return(
      <>
        <th><button id={"id"} className={"custom-thead-btn"} onClick={sortId}>ID</button></th>
        <th><button id={"title"} className={"custom-thead-btn"}>Заголовок</button></th>
        <th><button id={"desc"} className={"custom-thead-btn"}>Описание</button></th>
      </>
    );
  }
}

export default connect(
  state => ({
    posts: state.posts,
    currentPageTable: state.currentPageTable,
  }),
  dispatch => ({
    onAddCurrentPageTable: (item) => {
      dispatch({type: 'SAVE_CURRENT_PAGE', data: item})
    },
    onUpdateAllPosts: (item) => {
      dispatch({type: 'UPDATE_ALL_POSTS', data: item})
    }
  })
)(FilterData);
