import React from "react";
import {TemplateButton} from "../TemplateButton/TemplateButton";

const Pagination = (
  {
    postPerPage,
    totalPosts,
    paginate,
    prevPage,
    nextPage
  }
) => {

  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalPosts / postPerPage); i++ ) {
    pageNumbers.push(i);
  }

  return (
    <div className={"row"}>
      <div className={"center-block col-md-4"}>
        <ul className={"pagination"}>
          <li className={"page-item"}>
            <TemplateButton styleBtn={"link"} func={prevPage} content={"Назад"}/>
          </li>
          {
            pageNumbers.map((number) => (
              <li className={"page-item"} key={number}>
                <a href={"/#"} className={"page-link"} onClick={() => paginate(number)}>
                  {number}
                </a>
              </li>
            ))
          }
          <li className={"page-item"}>
            <TemplateButton styleBtn={"link"} func={nextPage} content={"Далее"}/>
          </li>
        </ul>
      </div>
    </div>
  )
}

export default Pagination;
