import React, { Component } from "react";
import {connect} from "react-redux";
import "./FilterData.css";

class FilterData extends Component {
  render() {
    const allPosts = this.props.posts;
    const isSorted = this.props.isSorted;

    const sortId = () => {
      const reverse = allPosts.sort().reverse();

      this.props.onDeletePosts();

      reverse.map(item => {
        this.props.onUpdateAllPosts(item);
      })
    }

    const sortTitle = () => {
      let reverse = allPosts.sort((a, b) => (a.title > b.title) ? 1 : -1);

      this.props.onDeletePosts();

      console.log(reverse);

      reverse.map(item => {
        this.props.onUpdateAllPosts(item);
      })


    }


    return(
      <>
        <th><button id={"id"} className={"custom-thead-btn"} onClick={sortId}>ID</button></th>
        <th><button id={"title"} className={"custom-thead-btn"} onClick={sortTitle}>Заголовок</button></th>
        <th><button id={"desc"} className={"custom-thead-btn"}>Описание</button></th>
      </>
    );
  }
}

export default connect(
  state => ({
    posts: state.posts,
    currentPageTable: state.currentPageTable,
    isSorted: state.isSorted,
  }),
  dispatch => ({
    onAddCurrentPageTable: (item) => {
      dispatch({type: 'SAVE_CURRENT_PAGE', data: item})
    },
    onUpdateAllPosts: (item) => {
      dispatch({type: 'UPDATE_ALL_POSTS', data: item})
    },
    onDeletePosts: () => {
      dispatch({type: 'DELETE_ALL_POSTS'})
    },
    onUpdateSort: (item) => {
      dispatch({type: 'UPDATE_SORT', data: item})
    }
  })
)(FilterData);
