import React from "react";
import {connect, useDispatch, useSelector} from "react-redux";

const Pagination = ({ postPerPage, totalPosts, paginate }) => {
  const pageNumbers = [];
  const dispatch = useDispatch();
  const currentPageTable = useSelector(state => state.currentPageTable);

  for (let i = 1; i <= Math.ceil(totalPosts / postPerPage); i++ ) {
    pageNumbers.push(i);
  }

  const nextPage = () => {
    const next = currentPageTable + 1;
    dispatch(next.action());
    // this.props.onAddCurrentPageTable(
    //   this.props.currentPageTable.index + 1 > 10
    //     ? 1
    //     : this.props.currentPageTable.index + 1
    // );
  }

  const prevPage = () => {
    const prev = currentPageTable - 1;
    dispatch(prev.action());
    // this.props.onAddCurrentPageTable(
    //   this.props.currentPageTable.index - 1 < 1
    //     ? 10
    //     : this.props.currentPageTable.index - 1
    // );
  }

  return (
    <div>
      <button className={"btn btn-primary"} onClick={prevPage}>Назад</button>
      <ul className={"pagination"}>
        {
          pageNumbers.map((number) => (
            <li className={"page-item"} key={number}>
              <a href={"/#"} className={"page-link"} onClick={() => paginate(number)}>
                {number}
              </a>
            </li>
          ))
        }
      </ul>
      <button className={"btn btn-primary"} onClick={nextPage}>Далее</button>
    </div>
  )
}

export default connect(
  state => ({
    posts: state.posts,
    currentPageTable: state.currentPageTable,
  }),
  dispatch => ({
    onAddCurrentPageTable: (item) => {
      dispatch({type: 'SAVE_CURRENT_PAGE', data: item})
    }
  })
)(Pagination);
