import React, { Component } from "react";
import { Table, Button } from "react-bootstrap";
import "./TableData.css";
import { connect } from "react-redux";


class TableData extends Component {
  render() {

    let countPages;

    return (
      <>
        <Table bordered>
          <thead className={"tbl-border"}>
          <tr className={"tbl-header"}>
            <th>ID</th>
            <th>Заголовок</th>
            <th>Описание</th>
          </tr>
          </thead>
          <tbody>
          {this.props.posts.map((item) => {
           const { userId, id, title, body } = item;

           countPages = id / 10;

           if (userId === 1) {
             return(
               <>
                 <tr className={id !== "" && title !== "" && body !== "" ? "full-content" : "empty-content"}>
                   <td className={"id-format"}>{id}</td>
                   <td className={"title-format"}>{title}</td>
                   <td className={"content-format"}>{body}</td>
                 </tr>
               </>
             )
           }


          })}

          </tbody>
        </Table>
        <div>
          <Button variant="link">Назад</Button>



          <Button variant="link">Далее</Button>
        </div>
      </>
    );
  }
}

export default connect(
  state => ({
    posts: state.posts,
  }),
  dispatch => ({
    onAddRedux: (item) => {
      dispatch({ type: 'ADD_TO_REDUX', data: item })
    },
  })
)(TableData);
